(89, 'pJoE7htzBekBdntvASd7', 'Simple Loaders', '<div class=\"page\">\r\n	<header class=\"header\">\r\n		<h1 class=\"header-title\">Simple CSS loaders</h1>\r\n		<p class=\"header-subtitle\">single html element css animation</p>\r\n	</header>\r\n	\r\n	<main class=\"container\">\r\n		<div class=\"item\">\r\n			<i class=\"loader --2\"></i>\r\n		</div>\r\n		<div class=\"item\">\r\n			<i class=\"loader --9\"></i>\r\n		</div>\r\n		<div class=\"item\">\r\n			<i class=\"loader --3\"></i>\r\n		</div>\r\n		\r\n		<div class=\"item\">\r\n			<i class=\"loader --4\"></i>\r\n		</div>\r\n		<div class=\"item\">\r\n			<i class=\"loader --1\"></i>\r\n		</div>\r\n		<div class=\"item\">\r\n			<i class=\"loader --5\"></i>\r\n		</div>\r\n		\r\n		<div class=\"item\">\r\n			<i class=\"loader --6\"></i>\r\n		</div>\r\n		<div class=\"item\">\r\n			<i class=\"loader --8\"></i>\r\n		</div>\r\n		<div class=\"item\">\r\n			<i class=\"loader --7\"></i>\r\n		</div>\r\n	</main>\r\n</div>', '.loader {\r\n	--color: white;\r\n	--size-mid: 6vmin;\r\n	--size-dot: 1.5vmin;\r\n	--size-bar: 0.4vmin;\r\n	--size-square: 3vmin;\r\n	\r\n	display: block;\r\n	position: relative;\r\n	width: 50%;\r\n	display: grid;\r\n	place-items: center;\r\n}\r\n\r\n.loader::before,\r\n.loader::after {\r\n	content: \'\';\r\n	box-sizing: border-box;\r\n	position: absolute;\r\n}\r\n\r\n/**\r\n	loader --1\r\n**/\r\n.loader.--1::before {\r\n	width: var(--size-mid);\r\n	height: var(--size-mid);\r\n	border: 4px solid var(--color);\r\n	border-top-color: transparent;\r\n	border-radius: 50%;\r\n	animation: loader-1 1s linear infinite;\r\n}\r\n\r\n.loader.--1::after {\r\n	width: calc(var(--size-mid) - 2px);\r\n	height: calc(var(--size-mid) - 2px);\r\n	border: 2px solid transparent;\r\n	border-top-color: var(--color);\r\n	border-radius: 50%;\r\n	animation: loader-1 0.6s linear reverse infinite;\r\n}\r\n\r\n@keyframes loader-1 {\r\n	100% {\r\n		transform: rotate(1turn);\r\n	}\r\n}\r\n\r\n/**\r\n	loader --2\r\n**/\r\n.loader.--2::before,\r\n.loader.--2::after {\r\n	width: var(--size-dot);\r\n	height: var(--size-dot);\r\n	background-color: var(--color);\r\n	border-radius: 50%;\r\n	opacity: 0;\r\n	animation: loader-2 0.8s cubic-bezier(0.2, 0.32, 0, 0.87) infinite;\r\n}\r\n\r\n.loader.--2::after {\r\n	animation-delay: 0.3s;\r\n}\r\n\r\n@keyframes loader-2 {\r\n	0%, 80%, 100% {\r\n		opacity: 0;\r\n	}\r\n	\r\n	33% {\r\n		opacity: 1;\r\n	}\r\n	\r\n	0%, 100% {\r\n		transform: translateX(-4vmin);\r\n	}\r\n	\r\n	90% {\r\n		transform: translateX(4vmin);\r\n	}\r\n}\r\n\r\n/**\r\n	loader --3\r\n**/\r\n.loader.--3::before,\r\n.loader.--3::after {\r\n	width: var(--size-dot);\r\n	height: var(--size-dot);\r\n	background-color: var(--color);\r\n	border-radius: 50%;\r\n	animation: loader-3 1.2s ease-in-out infinite;\r\n}\r\n\r\n.loader.--3::before {\r\n	left: calc(50% - 1.6vmin - var(--size-dot));\r\n}\r\n\r\n.loader.--3::after {\r\n	left: calc(50%   1.6vmin);\r\n	animation-delay: -0.4s;\r\n}\r\n\r\n@keyframes loader-3 {\r\n	0%, 100% {\r\n		transform: translateY(-2.6vmin);\r\n	}\r\n	\r\n	44% {\r\n		transform: translateY(2.6vmin);\r\n	}\r\n}\r\n\r\n/**\r\n	loader --4\r\n**/\r\n.loader.--4::before {\r\n	height: var(--size-bar);\r\n	width: 6vmin;\r\n	background-color: var(--color);\r\n	animation: loader-4 0.8s cubic-bezier(0, 0, 0.03, 0.9) infinite;\r\n}\r\n\r\n@keyframes loader-4 {\r\n	0%, 44%, 88.1%, 100% {\r\n		transform-origin: left;\r\n	}\r\n	\r\n	0%, 100%, 88% {\r\n		transform: scaleX(0);\r\n	}\r\n	\r\n	44.1%, 88% {\r\n		transform-origin: right;\r\n	}\r\n	\r\n	33%, 44% {\r\n		transform: scaleX(1);\r\n	}\r\n}\r\n\r\n/**\r\n	loader --5\r\n**/\r\n.loader.--5::before,\r\n.loader.--5::after {\r\n	height: 3vmin;\r\n	width: var(--size-bar);\r\n	background-color: var(--color);\r\n	animation: loader-5 0.6s cubic-bezier(0, 0, 0.03, 0.9) infinite;\r\n}\r\n\r\n.loader.--5::before {\r\n	left: calc(50% - 1vmin);\r\n	top: calc(50% - 3vmin);\r\n}\r\n\r\n.loader.--5::after {\r\n	left: calc(50%   1vmin);\r\n	top: calc(50% - 1vmin);\r\n	animation-delay: 0.2s;\r\n}\r\n\r\n@keyframes loader-5 {\r\n	0%, 88%, 100% {\r\n		opacity: 0;\r\n	}\r\n	\r\n	0% {\r\n		transform: translateY(-6vmin);\r\n	}\r\n	\r\n	33% {\r\n		opacity: 1;\r\n	}\r\n	\r\n	33%, 88% {\r\n		transform: translateY(3vmin);\r\n	}\r\n}\r\n\r\n/**\r\n	loader --6\r\n**/\r\n.loader.--6::before {\r\n	width: var(--size-square);\r\n	height: var(--size-square);\r\n	background-color: var(--color);\r\n	top: calc(50% - var(--size-square));\r\n	left: calc(50% - var(--size-square));\r\n	animation: loader-6 2.4s cubic-bezier(0, 0, 0.24, 1.21) infinite;\r\n}\r\n\r\n@keyframes loader-6 {\r\n	0%, 100% {\r\n		transform: none;\r\n	}\r\n	\r\n	25% {\r\n		transform: translateX(100%);\r\n	}\r\n	\r\n	50% {\r\n		transform: translateX(100%) translateY(100%);\r\n	}\r\n	\r\n	75% {\r\n		transform: translateY(100%);\r\n	}\r\n}\r\n\r\n/**\r\n	loader --7\r\n**/\r\n.loader.--7::before,\r\n.loader.--7::after {\r\n	width: var(--size-square);\r\n	height: var(--size-square);\r\n	background-color: var(--color);\r\n}\r\n\r\n.loader.--7::before {\r\n	top: calc(50% - var(--size-square));\r\n	left: calc(50% - var(--size-square));\r\n	animation: loader-6 2.4s cubic-bezier(0, 0, 0.24, 1.21) infinite;\r\n}\r\n\r\n.loader.--7::after {\r\n	top: 50%;\r\n	left: 50%;\r\n	animation: loader-7 2.4s cubic-bezier(0, 0, 0.24, 1.21) infinite;\r\n}\r\n\r\n@keyframes loader-7 {\r\n	0%, 100% {\r\n		transform: none;\r\n	}\r\n	\r\n	25% {\r\n		transform: translateX(-100%);\r\n	}\r\n	\r\n	50% {\r\n		transform: translateX(-100%) translateY(-100%);\r\n	}\r\n	\r\n	75% {\r\n		transform: translateY(-100%);\r\n	}\r\n}\r\n\r\n/**\r\n	loader --8\r\n**/\r\n.loader.--8::before,\r\n.loader.--8::after {\r\n	width: var(--size-dot);\r\n	height: var(--size-dot);\r\n	border-radius: 50%;\r\n	background-color: var(--color);\r\n}\r\n\r\n.loader.--8::before {\r\n	top: calc(50%   4vmin);\r\n	animation: loader-8-1 0.8s cubic-bezier(0.06, 0.01, 0.49, 1.18) infinite;\r\n}\r\n\r\n.loader.--8::after {\r\n	opacity: 0;\r\n	top: calc(50% - 2vmin);\r\n	animation: loader-8-2 0.8s cubic-bezier(0.46,-0.1, 0.27, 1.07) 0.2s infinite;\r\n}\r\n\r\n@keyframes loader-8-1 {\r\n	0%, 55%, 100% {\r\n		opacity: 0;\r\n	}\r\n	\r\n	0% {\r\n		transform: scale(0.2);\r\n	}\r\n	\r\n	22% {\r\n		opacity: 1;\r\n	}\r\n	\r\n	33%, 55% {\r\n		transform: scale(1) translateY(-6vmin);\r\n	}\r\n}\r\n\r\n@keyframes loader-8-2 {\r\n	0%, 100% {\r\n		opacity: 0;\r\n	}\r\n	\r\n	33% {\r\n		opacity: 0.3;\r\n	}\r\n	\r\n	0% {\r\n		transform: scale(0);\r\n	}\r\n	\r\n	100% {\r\n		transform: scale(4);\r\n	}\r\n}\r\n\r\n/**\r\n	loader --9\r\n**/\r\n.loader.--9::before,\r\n.loader.--9::after {\r\n	width: var(--size-dot);\r\n	height: var(--size-dot);\r\n	border-radius: 50%;\r\n	background-color: var(--color);\r\n	animation: loader-9 0.42s cubic-bezier(0.39, 0.31, 0, 1.11) infinite;\r\n}\r\n\r\n.loader.--9::before {\r\n	left: calc(50% - var(--size-dot) - 1.6vmin);\r\n}\r\n\r\n.loader.--9::after {\r\n	left: calc(50%   1.6vmin);\r\n	animation-delay: 0.12s;\r\n}\r\n\r\n@keyframes loader-9 {\r\n	0%, 100% {\r\n		opacity: 0;\r\n	}\r\n	\r\n	0% {\r\n		transform: translate(-4vmin, -4vmin);\r\n	}\r\n	\r\n	66% {\r\n		opacity: 1;\r\n	}\r\n	\r\n	66%, 100% {\r\n		transform: none;\r\n	}\r\n}\r\n\r\n/**\r\n	miscs\r\n**/\r\n\r\n.container {\r\n	display: grid;\r\n	grid-template-columns: repeat(3, 18vmin);\r\n	grid-template-rows: repeat(3, 18vmin);\r\n	grid-gap: 1vmin;\r\n}\r\n\r\n.item	{\r\n	background: rgba(255, 255, 255, 0.1);\r\n	display: grid;\r\n	place-items: center;\r\n	border-radius: 4px;\r\n	transition: opacity 0.4s ease;\r\n}\r\n\r\n.container:hover .item {\r\n	opacity: 0.3;\r\n}\r\n\r\n.container:hover .item:hover {\r\n	opacity: 1;\r\n}\r\n\r\n.page {\r\n	margin: auto;\r\n}\r\n\r\n.header {\r\n	margin-bottom: 4vmin;\r\n}\r\n\r\n.header-title {\r\n	font-size: 3.75vmin;\r\n}\r\n\r\n.header-subtitle {\r\n	font-size: 2vmin;\r\n	text-transform: uppercase;\r\n	opacity: 0.6;\r\n}\r\n\r\nhtml, body {\r\n	display: flex;\r\n	width: 100%;\r\n	height: 100%;\r\n	background-image: linear-gradient(to right top, #051937, #004d7a, #008793, #00bf72, #a8eb12);\r\n	font-family: \'Noto Sans\', sans-serif;\r\n	color: white;\r\n	text-align: center;\r\n	letter-spacing: 0.3px;\r\n}', '', '1', '9', 'base', 'component__658e2bb1ba5f0'),
