nst overlay = document.createElement(\"div\");\r\n  overlay.classList.add(\"overlay\");\r\n\r\n  // Agrega el overlay al final del documento\r\n  document.body.appendChild(overlay);\r\n\r\n  // Función para mostrar el overlay y el título correspondiente\r\n  function showOverlay(title, input) {\r\n    overlay.style.display = \"block\";\r\n    input.classList.add(\"absolute-input\");\r\n    titles.forEach((t) => {\r\n      if (title != t) {\r\n        t.style.display = \"none\";\r\n      }\r\n    });\r\n  }\r\n\r\n  // Función para ocultar el overlay y todos los títulos\r\n  function hideOverlay() {\r\n    overlay.style.display = \"none\";\r\n    inputs.map((x) => {\r\n      x.classList.remove(\"absolute-input\");\r\n    });\r\n    titles.forEach((t) => {\r\n      t.style.display = \"block\";\r\n    });\r\n  }\r\n\r\n  // Agrega los eventos de clic para mostrar y ocultar el overlay\r\n  inputs.map((x) => {\r\n    console.log(x);\r\n    x.addEventListener(\"focus\", function () {\r\n      let title = x.parentElement.querySelector(\".title\");\r\n      showOverlay(title, x);\r\n    });\r\n    x.addEventListener(\"blur\", (e) => {\r\n      hideOverlay();\r\n    });\r\n  });\r\n\r\n  // TOOLTIPS\r\n  const inputfield1 = document.querySelector(\"#inputfield1\");\r\n  const tooltip1 = document.querySelector(\"#tooltip1\");\r\n  const validationMessage = document.getElementById(\"validationMessage\");\r\n  // const inputfield2 = document.querySelector(\"#inputfield2\");\r\n  // const tooltip2 = document.querySelector(\"#tooltip2\");\r\n\r\n  const inputfield1Placeholder = inputfield1.getAttribute(\"placeholder\");\r\n\r\n  inputfield1.addEventListener(\"focus\", function () {\r\n    this.removeAttribute(\"placeholder\");\r\n  });\r\n\r\n  inputfield1.addEventListener(\"blur\", function () {\r\n    this.setAttribute(\"placeholder\", inputfield1Placeholder);\r\n  });\r\n\r\n  inputfield1.addEventListener(\"input\", function () {\r\n    const inputLength = this.value.length;\r\n    if (inputLength < this.minLength) {\r\n      validationMessage.textContent = \"Mínimo 60 caracteres*\";\r\n    } else {\r\n      validationMessage.textContent = \"\";\r\n    }\r\n  });\r\n\r\n  const calcularPosicionTooltip = (inputfield, tooltip) => {\r\n    // Calculamos las coordenadas del inputfield.\r\n    const x = inputfield.offsetLeft;\r\n    const y = inputfield.offsetTop;\r\n    // Calculamos el tamaño del tooltip.\r\n    const anchoTooltip = tooltip.clientWidth;\r\n    const altoTooltip = tooltip.clientHeight;\r\n    // Calculamos donde posicionaremos el tooltip.\r\n    const izquierda = x - anchoTooltip / 2 ___plus___ inputfield.offsetWidth / 2;\r\n    const arriba = y - altoTooltip - 20;\r\n    tooltip.style.left = `${izquierda}px`;\r\n    tooltip.style.top = `${arriba}px`;\r\n  };\r\n\r\n  window.addEventListener(\"load\", () => {\r\n    calcularPosicionTooltip(inputfield1, tooltip1);\r\n    //calcularPosicionTooltip(inputfield2, tooltip2);\r\n  });\r\n\r\n  window.addEventListener(\"resize\", () => {\r\n    calcularPosicionTooltip(inputfield1, tooltip1);\r\n    //calcularPosicionTooltip(inputfield2, tooltip2);\r\n  });\r\n\r\n  inputfield1.addEventListener(\"focus\", () => {\r\n    tooltip1.classList.add(\"activo\");\r\n    calcularPosicionTooltip(inputfield1, tooltip1);\r\n  });\r\n\r\n  inputfield1.addEventListener(\"blur\", () => {\r\n    tooltip1.classList.remove(\"activo\");\r\n  });\r\n\r\n  // inputfield2.addEventListener(\"focus\", () => {\r\n  //   tooltip2.classList.add(\"activo\");\r\n  //   calcularPosicionTooltip(inputfield2, tooltip2);\r\n  // });\r\n\r\n  // inputfield2.addEventListener(\"blur\", () => {\r\n  //   tooltip2.classList.remove(\"activo\");\r\n  // });\r\n});', '1', '11', '159', 'component__65b27dfb8ef96'),
